import React, { useState, useEffect } from 'react'
import SiteNav from '../components/SiteNav';
import SiteFooter from '../components/SiteFooter';
import { Auth } from 'aws-amplify';
import { S3Image } from 'aws-amplify-react';
import '../css/main.css';
import listUsers from './listUsers';
import MUIDataTable from 'mui-datatables'
const AWS = require('aws-sdk');

// AWS.config.update({region:'us-east-1'});
// var myConfig = new AWS.Config({
//   credentials: myCredentials, region: 'us-east-1'
// });
// var myCredentials = new AWS.CognitoIdentityCredentials({
//     IdentityPoolId:'us-east-1:98d665d1-adfe-4f34-a3d9-1842d1c30ead'
// });
// AWS.config = myConfig
AWS.config.update({ region: 'us-east-1', 'accessKeyId': 'AKIAXJPGEEDHGTRSHOKC', 'secretAccessKey': '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' });
var cognito = new AWS.CognitoIdentityServiceProvider();

const options = {
  filter: false,
  search: false,
  viewColumns: false,
  print: false,
  download: false,
  filterType: 'dropdown',
  responsive: 'stacked',
  resizableColumns: false,
  rowsPerPageOptions: [15, 30, 50],
  rowsPerPage: 15,
  selectableRows: 'none',
}

const columns = [
  
    {
      name: 'Username',
      label: 'Username',
      options: {
        filter: true,
      },
    },
    {
      name: 'UserStatus',
      label: 'UserStatus',
      options: {
        filter: true,
      },
    },
  
]
class Profile extends React.Component {
    constructor(props) {
        super(props);
        console.log("props")
        console.log(props)
        this.state = {
            user: {
                attributes: {
                    email: 'me@example.com',
                    phone_number: '+1123456789'
                }
            },
            
        }
    }
    

     list = async () => {
          
      console.log("list user method")
      const params = {
        UserPoolId: 'us-east-1_NBUlVCEwF',
        AttributesToGet: [],
        Filter: '',
        Limit: 10,
    };
    

    cognito.listUsers(params, (err, data) => {
      if (err) {
          console.log(err);
          
      }
      else {
          console.log("data", data);
             
      }
  }) 
        
    }
    componentDidMount() {
        this.list()
        Auth.currentAuthenticatedUser().then(user => {
            console.log('Cognito User', user);
            this.setState({user, image_key: 'profile-' + user.attributes.sub + '.jpg'});
        });;
        

    }

    async onImageLoad(url) {
        console.error('onImageLoad is not yet implemented');
    }

    render() {
      return (
      <div className="page-unicorns">
        <header className="site-header">
          <div>
            
          {/* <S3Image imgKey={this.state.image_key} onLoad={(url) => this.onImageLoad(url)} picker/> */}
    		<table align="center">
    		<tbody>
             <tr>
             <td>E-mail:</td>
             <td>{this.state.user.attributes.email}</td>
             </tr>
              <tr>
             <td>Phone:</td>
             <td>{this.state.user.attributes.phone_number}</td>
             </tr>
             </tbody>
            </table>
            <React.Fragment>
      <MUIDataTable
              id='externalTable'
              data={[]}
              // data={[{name:'hello',email:'hai'},{name:'ok',email:'he'}]}
              data-testid='externalTable'
              columns={columns}
              options={options}
        />
      </React.Fragment>
    	</div>
          <SiteNav/>
        </header>
        
        <SiteFooter/>
        
     
      
      </div>
     
    );
  }
 
}

export default Profile;
