import React from 'react';
import SiteNav from '../components/SiteNav';
import SiteFooter from '../components/SiteFooter';
import { Auth } from 'aws-amplify';
import { S3Image } from 'aws-amplify-react';
import '../css/main.css';
import listUsers from './listUsers';
const AWS = require('aws-sdk');
AWS.config.update({region:'us-east-1'});
var myCredentials = new AWS.CognitoIdentityCredentials({
    IdentityPoolId:'us-east-1:98d665d1-adfe-4f34-a3d9-1842d1c30ead'
});
const cognitoIdentityServiceProvider = new AWS.CognitoIdentityServiceProvider({
    region: 'us-east-1',
    credentials: myCredentials
});


class Profile extends React.Component {
    constructor(props) {
        super(props);
        console.log("props")
        console.log(props)
        this.state = {
            user: {
                attributes: {
                    email: 'me@example.com',
                    phone_number: '+1123456789'
                }
            }
        }
    }
     listuser = async () => {
          
      console.log("list user method")
      let params = {
        UserPoolId: 'us-east-1_NBUlVCEwF',
        AttributesToGet: [],
        Filter: '',
        Limit: 10,
    };

    

    return cognitoIdentityServiceProvider.listUsers(params).promise().then(data => {
        console.log("entering")
        data.Users = data.Users.map(user => {
            let users = {
                user_id: user.Username,
                name: '',
                email: '',
                enabled: user.Enabled,
                status: user.UserStatus
            };

           
            return users;
        });
        console.log("datais")
        console.log(data)
        return data;
     });
        
    }
    componentDidMount() {
      
        Auth.currentAuthenticatedUser().then(user => {
            console.log('Cognito User', user);
            this.setState({user, image_key: 'profile-' + user.attributes.sub + '.jpg'});
        });;
        

    }

    async onImageLoad(url) {
        console.error('onImageLoad is not yet implemented');
    }

    render() {
      return (<div className="page-unicorns">
        <header className="site-header">
          <div>
            
          {/* <S3Image imgKey={this.state.image_key} onLoad={(url) => this.onImageLoad(url)} picker/> */}
    		<table align="center">
    		<tbody>
             <tr>
             <td>E-mail:</td>
             <td>{this.state.user.attributes.email}</td>
             </tr>
              <tr>
             <td>Phone:</td>
             <td>{this.state.user.attributes.phone_number}</td>
             </tr>
             </tbody>
            </table>
    	</div>
          <SiteNav/>
        </header>
        
        <SiteFooter/>
      </div>
    );
  }
}

export default Profile;
